INSERT INTO `accommodation` (`name`, `image`, `description`, `price`, `status`, `createdAt`, `updatedAt`) VALUES
	('Quarto Casal', 'https://raw.githubusercontent.com/rtof83/tex-js-grupo4-final/main/src/assets/images/QuartoCasal.png', 'Nossa opção para você que busca um espaço mais intimista. Com design em madeira e vidro, destacando a luz natural, possui vista para o mar. Perfeito para um final de semana com quem você ama!', 190.00, 1, '2023-03-19 14:55:29', '2023-03-19 14:55:29'),
	('Quarto Executivo', 'https://raw.githubusercontent.com/rtof83/tex-js-grupo4-final/main/src/assets/images/Su%C3%ADtePresidencial.jpg', 'Para você que busca conforto e mais espaço, a suíte executiva é a ideal. Além do design em madeira, possui paredes de vidro que permitem você acordar todas as manhãs com uma vista sensacional.', 350.00, 1, '2023-03-19 14:55:29', '2023-03-19 14:55:29'),
	('Quarto Presidencial Luxo', 'https://raw.githubusercontent.com/rtof83/tex-js-grupo4-final/main/src/assets/images/quarto_hotel.jpg', 'Nossa principal acomodação, a suíte presidencial luxo possui 1.132 metros quadrados, design intimista com cores sóbrias e duas suítes com banheira de hidromassagem para a sua inteira disposição. Não deixe de conferir!', 600.00, 1, '2023-03-19 14:55:29', '2023-03-19 14:55:29'),
	('Quarto Litoral', 'https://raw.githubusercontent.com/rtof83/tex-js-grupo4-final/main/src/assets/images/QuartoLuxo.jpg', 'Nossa opção para você que busca uma excelente vista para o mar! Desfrute de todo o nosso conforto com uma visão privilegiada do nosso litoral!', 800.00, 1, '2023-03-19 14:55:29', '2023-03-19 14:55:29'),
	('Quarto Weekend', 'https://raw.githubusercontent.com/rtof83/tex-js-grupo4-final/main/src/assets/images/suite-estudio-3.jpg', 'Para você que busca uma opção confortável e simples. Nosso quarto weekend é perfeito para você que busca um lugar para descansar enquanto curte o final de semana em nossa cidade!', 400.00, 1, '2023-03-19 14:55:29', '2023-03-19 14:55:29'),
	('Quarto Atlantic', 'https://raw.githubusercontent.com/rtof83/tex-js-grupo4-final/main/src/assets/images/suite-executiva-4.jpg', 'Um dos nossos melhores quartos! Possui um design em madeira, possui paredes de vidro que permitem você acordar todas as manhãs com uma vista sensacional.', 800.00, 1, '2023-03-19 14:55:29', '2023-03-19 14:55:29'),
	('Quarto Holiday', 'https://raw.githubusercontent.com/rtof83/tex-js-grupo4-final/main/src/assets/images/1604663246841.jpg', 'Para você que busca uma opção confortável e simples. Nosso quarto weekend é perfeito para você que busca um lugar para descansar enquanto curte o feriado em nossa cidade!', 300.00, 1, '2023-03-19 14:55:29', '2023-03-19 14:55:29'),
	('Quarto Honey Moon', 'https://raw.githubusercontent.com/rtof83/tex-js-grupo4-final/main/src/assets/images/suite-presidencial-1.jpg', 'Quarto perfeito para passar a lua de mel com quem você ama! Desfrute do nosso serviço de quarto plus, vista para o mar e todo o nosso conforto nesse momento tão especial!', 500.00, 1, '2023-03-19 14:55:29', '2023-03-19 14:55:29'),
	('Quarto Day Off', 'https://raw.githubusercontent.com/rtof83/tex-js-grupo4-final/main/src/assets/images/Su%C3%ADtePresidencial.jpg', 'Quarto perfeito para descansar, com serviço de spa incluso, massagem especializada e todo o nosso conforto para tornar o seu dia de folga especial!', 100.00, 1, '2023-03-19 14:55:29', '2023-03-19 14:55:29')
